import React, { useState } from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { login } from "../../action/auth";

const Login = ({ login }) => {
  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });

  const { email, password } = formData;

  const onChange = e => {
    setFormData({ ...formData, [e.target.name]: [e.target.value] });
  };

  const onSubmit = e => {
    e.preventDefault();
    login({ email, password });
  };

  return (
    <div class="app-container app-theme-white body-tabs-shadow">
      <div class="app-container">
        <div
          style={{ margin: "auto" }}
          class="h-100 d-flex bg-white justify-content-center align-items-center col-md-12 col-lg-8"
        >
          <div class="mx-auto app-login-box col-sm-12 col-md-10 col-lg-9">
            <div class="app-logo"></div>
            <h4 class="mb-0">
              <span class="d-block">Welcome back,</span>
              <span>Please sign in to your account.</span>
            </h4>
            <h6 class="mt-3">
              No account?{" "}
              <Link to={"/"} class="text-primary">
                Sign up now
              </Link>
            </h6>
            <div class="divider row"></div>
            <div>
              <form class="" onSubmit={e => onSubmit(e)}>
                <div class="form-row">
                  <div class="col-md-6">
                    <div class="position-relative form-group">
                      <label for="exampleEmail" class="">
                        Correo Electronico
                      </label>
                      <input
                        name="email"
                        id="exampleEmail"
                        placeholder="Introduzca su correo electronico..."
                        type="email"
                        onChange={e => onChange(e)}
                        value={email}
                        class="form-control"
                      />
                    </div>
                  </div>
                  <div class="col-md-6">
                    <div class="position-relative form-group">
                      <label for="examplePassword" class="">
                        Contraseña
                      </label>
                      <input
                        name="password"
                        id="examplePassword"
                        placeholder="Introduzca su contraseña..."
                        type="password"
                        onChange={e => onChange(e)}
                        value={password}
                        class="form-control"
                      />
                    </div>
                  </div>
                </div>
                <div class="position-relative form-check">
                  <input
                    name="check"
                    id="exampleCheck"
                    type="checkbox"
                    class="form-check-input"
                  />
                  <label for="exampleCheck" class="form-check-label">
                    Recordarme
                  </label>
                </div>
                <div class="divider row"></div>
                <div class="d-flex align-items-center">
                  <div class="ml-auto">
                    <Link to={"/"} class="btn-lg btn btn-link">
                      Recuperar Contraseña
                    </Link>
                    <button class="btn btn-primary btn-lg" type="submit">
                      Iniciar
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

Login.propTypes = {
  login: PropTypes.func.isRequired
};

export default connect(
  null,
  { login }
)(Login);
